import React from 'react'
import { useState } from 'react'
import "./Fuelquoteform.css"

class FuelQuoteForm extends React.Component {

    // Set by the pricing module
    pricePerGallon = 34.34;

    // Set by the client profile
    deliveryAddress = "2115 Heather Green Dr."

    constructor(props) {
        super(props);
        this.state = {
            gallons: 0,
            date: "",
            total: 0.00,

            rows: []
        };

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleAddRow = () => {
        const item = {
            date: this.state.date,
            gallons: this.state.gallons,
            total: this.state.total
        };
        this.setState({
            rows: [...this.state.rows, item]
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        this.setState({
            total: Math.round(this.pricePerGallon * this.state.gallons * 100) / 100
        })
        this.handleAddRow();
    }

    heading = [
        {
            id: 0,
            label: "Date"
        },
        {
            id: 1,
            label: "Gallons"
        },
        {
            id: 2,
            label: "Delivery Date"
        },
        {
            id: 3,
            label: "PPG"
        },
        {
            id: 4,
            label: "Total"
        }];

    // From: https://stackoverflow.com/a/55556258/817946
    currencyFormat(num) {
        return '$' + num.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
    }

    render() {
        return (
            <div className="container" >
                <div className="quoteForm">
                    <h3>Pricing Form</h3>
                    <form onSubmit={this.handleSubmit}>
                        <label>Gallons:
                            <input type="number"
                                className="numberOfGallons"
                                value={this.state.gallons}
                                name="gallons"
                                onChange={(e) => this.handleInputChange(e)}
                            />
                        </label>
                        <br />
                        <label>
                            Delivery Address:
                            <input type="text"
                                className="deliveryAddress"
                                value={this.deliveryAddress}
                                readOnly={true}
                            />
                        </label>
                        <br />
                        <label>Delivery date
                            <input type="date"
                                className="deliveryDate"
                                value={this.state.date}
                                onChange={(e) => this.handleInputChange(e)}
                            />
                        </label>
                        <br />
                        <label>Price per gallon
                            <input
                                className="pricePerGallon"
                                value={this.pricePerGallon}
                                readOnly={true}
                            />
                        </label>
                        <br />
                        <input type="submit" />
                    </form>
                    <h4 className="totalPrice">{this.currencyFormat(this.state.total)}</h4>
                </div>
                <div className="table-container">
                    <table className="quote-table">
                        <thead>
                            <tr key="head" >
                                {this.heading.map((item, key) => {
                                    return (
                                        <td key={key}><center>{item.label}</center></td>
                                    )
                                })}
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.rows.map((item, key) => {
                                return (
                                    <tr id="addr0" key={key} >
                                        <td><center>{item.gallons}</center></td>
                                        <td>{item.age}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div >
            </div>
        )
    }
}

export default FuelQuoteForm;
