import React from 'react'
import { useState } from 'react'
import "./Fuelquoteform.css"

export default function FuelQuoteForm() {
    const [gallons, setGallons] = useState(0);
    const [date, setDeliveryDate] = useState("");
    const [total, setTotal] = useState(0.00);
    const [rows, setRows] = useState([])

    // Set by the pricing module
    const pricePerGallon = 34.34;

    // Set by the client profile
    const deliveryAddress = "2115 Heather Green Dr."

    const handleSubmit = (event) => {
        event.preventDefault();
        setTotal(Math.round(pricePerGallon * gallons * 100) / 100)
    }

    // From: https://stackoverflow.com/a/55556258/817946
    function currencyFormat(num) {
        return '$' + num.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
    }

    const handleChange = idx => e => {
        const { name, value } = e.target;
        const rows = [...this.state.rows];
        rows[idx] = {
            [name]: value
        };
        this.setState({
            rows
        });
    };

    const handleAddRow = () => {
        const item = {
            name: "",
            mobile: ""
        };
        this.setState({
            rows: [...this.state.rows, item]
        });
    };

    // Temp state var until I implement Redux
    // for table rows


    const heading = [
        {
            id: 0,
            label: "Quote Data"
        },
        {
            id: 1,
            label: "Gallons"
        },
        {
            id: 2,
            label: "Delivery Date"
        },
        {
            id: 3,
            label: "PPG"
        },
        {
            id: 4,
            label: "Total"
        }];


    return (
        <div className="container">
            <div className="quoteForm">
                <h3>Pricing Form</h3>
                <form onSubmit={handleSubmit}>
                    <label>Gallons:
                        <input type="number"
                            className="numberOfGallons"
                            value={gallons}
                            onChange={(e) => setGallons(e.target.value)}
                        />
                    </label>
                    <br />
                    <label>
                        Delivery Address:
                        <input type="text"
                            className="deliveryAddress"
                            value={deliveryAddress}
                            readOnly={true}
                        />
                    </label>
                    <br />
                    <label>Delivery date
                        <input type="date"
                            className="deliveryDate"
                            value={date}
                            onChange={(e) => setDeliveryDate(e.target.value)}
                        />
                    </label>
                    <br />
                    <label>Price per gallon
                        <input
                            className="pricePerGallon"
                            value={pricePerGallon}
                            readOnly={true}
                        />
                    </label>
                    <br />
                    <input type="submit" />
                </form>
                <h4 className="totalPrice">{currencyFormat(total)}</h4>
            </div>
            <div className="quoteTable">


                <div className="table-container">
                    <table className="quote-table">
                        <thead>
                            <tr key="head" >
                                {heading.map((item, key) => {
                                    return (
                                        <td key={key}><center>{item.label}</center></td>
                                    )
                                })}
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.rows.map((item, key) => {
                                return (
                                    <tr id="addr0" key={key} >
                                        <td><center>{item.name}</center></td>
                                        <td>{item.age}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div >
                )

            </div>
        </div>
    )
}
