import React from 'react'
import { useState } from 'react'
import "./Fuelquoteform.css"

class FuelQuoteForm extends React.Component {

    // Set by the pricing module
    pricePerGallon = 34.34;

    // Set by the client profile
    deliveryAddress = "2115 Heather Green Dr."

    constructor(props) {
        super(props);
        this.state = {
            gallons: 0,
            date: "",
            total: 0.00
        };

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }


    handleSubmit = (event) => {
        event.preventDefault();
        this.state.total = Math.round(this.pricePerGallon * this.state.gallons * 100) / 100
        //this.props.handleData(this.state.)
    }

    // From: https://stackoverflow.com/a/55556258/817946
    currencyFormat(num) {
        return '$' + num.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
    }

    render() {
        return(
        <div className="container" >
            <div className="quoteForm">
                <h3>Pricing Form</h3>
                <form onSubmit={handleSubmit}>
                    <label>Gallons:
                        <input type="number"
                            className="numberOfGallons"
                            value={gallons}
                            onChange={(e) => setGallons(e.target.value)}
                        />
                    </label>
                    <br />
                    <label>
                        Delivery Address:
                        <input type="text"
                            className="deliveryAddress"
                            value={deliveryAddress}
                            readOnly={true}
                        />
                    </label>
                    <br />
                    <label>Delivery date
                        <input type="date"
                            className="deliveryDate"
                            value={date}
                            onChange={(e) => setDeliveryDate(e.target.value)}
                        />
                    </label>
                    <br />
                    <label>Price per gallon
                        <input
                            className="pricePerGallon"
                            value={pricePerGallon}
                            readOnly={true}
                        />
                    </label>
                    <br />
                    <input type="submit" />
                </form>
                <h4 className="totalPrice">{currencyFormat(total)}</h4>
            </div>
        </div>
        )
    }
}
