import React from 'react'
import { useState } from 'react'
import ReactDOM from 'react-dom'
import "./Fuelquoteform.css"

export default function FuelQuoteForm() {
    const [name, setName] = useState("");
    const [gallons, setGallons] = useState("");
    const [date, setDeliveryDate] = useState("");
    const [total, setTotal] = useState("");

    // Set by the pricing module
    const pricePerGallon = 34.34;

    // Set by the client profile
    const deliveryAddress = "2115 Heather Green Dr."

    const handleSubmit = (event) => {
        event.preventDefault();
        setTotal(pricePerGallon * gallons)
    }
    return (
        <div className="quoteForm">
            <form onSubmit={handleSubmit}>
                <label>Gallons:
                    <input type="number"
                        class="numberOfGallons"
                        value={gallons}
                        onChange={(e) => setGallons(e.target.value)}
                    />
                </label>
                <br />
                <label>
                    Delivery Address:
                    <input type="text"
                        class="deliveryAddress"
                        value={deliveryAddress}
                    />
                </label>
                <br />
                <label>Delivery date
                    <input type="date"
                        class="deliveryDate"
                        value={date}
                        onChange={(e) => setDeliveryDate(e.target.value)}
                    />
                </label>
                <br />
                <label>Price per gallon
                    <input
                        value={pricePerGallon}
                    />
                </label>
                <br />
                <input type="submit" />
            </form>
            <h4 className="totalPrice">{total}</h4>
        </div>
    )
}
